import React, { Component } from "react";
import { Block } from "@material-ui/icons";

function makePhoneNum(input) {
  var inputArray = input.split("");
  inputArray.splice(3, 0, ".");
  inputArray.splice(7, 0, ".");
  return inputArray.join("");
}

function makeDepartment(input) {
  var inputDepartment = input.split(" ");
  return inputDepartment.join(" ");
}

export default class Output extends Component {
  constructor(props) {
    super(props);
    this.titleRef = React.createRef;
    this.myDep = React.createRef;
    this.state = {
      value: "",
      copied: false,
      titleWidth: 0,
      depWidth: 0,
      widthSum: 0,
    };
  }

  componentDidMount() {
    console.log(this.titleRef);
  }

  componentDidUpdate() {
    console.log(this.titleRef);
  }
  render() {
    const Pronouns = (
      <span className="pronouns">
        ({this.props.globalState.pronouns.subject}
        {"/"}
        {this.props.globalState.pronouns.object}
        {"/"}
        {this.props.globalState.pronouns.possessive})
        <br />
      </span>
    );

    var myTitle = this.props.globalState.title;
    var titleArray = myTitle.split(" ");
    myTitle = titleArray.join("\xa0");

    const Job = (
      <span
        className="job"
        style={{
          display: "block",
        }}
      >
        <div>
          <span id="title">{myTitle}</span>
        </div>
        <div>
          <span id="department">
            {makeDepartment(this.props.globalState.department)}
          </span>
        </div>
      </span>
    );

    const OfficeNum = (
      <span className="office-num">
        <br />
        {makePhoneNum(this.props.globalState.officePhoneNum)}
        {" office"}
      </span>
    );

    const CellNum = (
      <span className="cell-num">
        <br />
        {makePhoneNum(this.props.globalState.cellPhoneNum)}
        {" cell"}
      </span>
    );

    const addressStr = (
      <span className="address">{this.props.globalState.address}</span>
    );

    const landAcknowledgement = (
      <table
            cellPadding="0"
            cellSpacing="0"
            style={{
              fontFamily: '"Arial"',
              fontSize: "14px",
              color: "#716C6B",
              display: "block",
              width: "100%",
              maxWidth: "500px",
            }}
          >
            <tbody>
              <tr>
                <td
                  style={{
                    fontFamily: '"Arial',
                    fontSize: "12px",
                    color: "#716C6B",
                    paddingTop: "20px",
                  }}
                >
                  The Northwestern campus sits on the traditional homelands of
                  the people of the Council of Three Fires (the Ojibwe,
                  Potawatomi, and Odawa), as well as the Menominee, Miami, and
                  Ho-Chunk nations. Learn{" "}
                  <a href="https://www.northwestern.edu/native-american-and-indigenous-peoples/about/Land%20Acknowledgement.html">
                    more
                  </a>
                  .
                </td>
              </tr>
            </tbody>
          </table>
    )

    // create gradDesignation
    if (this.props.globalState.isGradAlum) {
      var gradDesignation = `${
        this.props.globalState.isUndergradAlum ? ", " : " "
      }`;
      for (var i = 0; i < this.props.globalState.gradInfo.length; i++) {
        if (i < this.props.globalState.gradInfo.length - 1) {
          if (i === 0) {
            gradDesignation =
              gradDesignation +
              ", " +
              "’" +
              this.props.globalState.gradInfo[i].year.toString().slice(-2) +
              " " +
              this.props.globalState.gradInfo[i].degree +
              ", ";
          } else {
            gradDesignation =
              gradDesignation +
              "’" +
              this.props.globalState.gradInfo[i].year.toString().slice(-2) +
              " " +
              this.props.globalState.gradInfo[i].degree +
              ", ";
          }
        } else {
          gradDesignation =
            gradDesignation +
            "’" +
            this.props.globalState.gradInfo[i].year.toString().slice(-2) +
            " " +
            this.props.globalState.gradInfo[i].degree;
        }
      }
    }

    var undergradDesignation = "";
    for (var i = 0; i < this.props.globalState.underGradInfo.length; i++) {
      undergradDesignation =
        undergradDesignation +
        " ’" +
        this.props.globalState.underGradInfo[i].year.toString().slice(-2);
    }

    // create parentDesignation
    if (this.props.globalState.isParentAlum) {
      var parentDesignation = `${
        this.props.globalState.isUndergradAlum ||
        this.props.globalState.isGradAlum
          ? ", "
          : " "
      }`;
      for (var i = 0; i < this.props.globalState.parentInfo.length; i++) {
        if (i < this.props.globalState.parentInfo.length - 1) {
          if (i === 0) {
            parentDesignation =
              parentDesignation +
              "’" +
              this.props.globalState.parentInfo[i].year.toString().slice(-2) +
              " " +
              this.props.globalState.parentInfo[i].degree +
              ", ";
          } else {
            parentDesignation =
              parentDesignation +
              "’" +
              this.props.globalState.parentInfo[i].year.toString().slice(-2) +
              " " +
              this.props.globalState.parentInfo[i].degree +
              ", ";
          }
        } else {
          parentDesignation =
            parentDesignation +
            "’" +
            this.props.globalState.parentInfo[i].year.toString().slice(-2) +
            " " +
            this.props.globalState.parentInfo[i].degree;
        }
      }
    }

    return (
      <section className="sig-result">
        <div className="sig-result__wrapper">
          <table
            cellPadding="0"
            cellSpacing="0"
            style={{
              borderTop: "1px solid #4e2a84",
              borderBottom: "1px solid #4e2a84",
              fontFamily: '"Arial"',
              fontSize: "14px",
              color: "#716C6B",
              display: "block",
              width: "100%",
              maxWidth: "fit-content",
            }}
          >
            <tbody>
              <tr>
                <td
                  style={{
                    fontFamily: '"Arial"',
                    fontSize: "16px",
                    color: "#4e2a84",
                    paddingTop: "10px",
                    lineHeight: "1.2",
                  }}
                >
                  <strong>
                    {this.props.globalState.firstName}{" "}
                    {`${
                      this.props.globalState.middleName.length > 0
                        ? this.props.globalState.middleName + " "
                        : ""
                    }`}
                    {this.props.globalState.lastName}
                    {this.props.globalState.isUndergradAlum &&
                    undergradDesignation.length > 3
                      ? undergradDesignation
                      : ""}
                    {this.props.globalState.isGradAlum &&
                    gradDesignation.length > 5
                      ? gradDesignation
                      : ""}
                    {this.props.globalState.isParentAlum &&
                      parentDesignation.length > 5 &&
                      parentDesignation}
                  </strong>
                </td>
              </tr>
              <tr>
                <td
                  style={{
                    fontFamily: '"Arial"',
                    fontSize: "14px",
                    color: "#716C6B",
                    paddingTop: "10px",
                    lineHeight: "1.37",
                  }}
                >
                  {this.props.globalState.pronouns.subject.length > 0 &&
                    Pronouns}
                  {this.props.globalState.title.length > 0 && Job}
                  <div>
                    <span className="org">{this.props.globalState.org}</span>
                  </div>
                  <div>
                    <span className="nu">Northwestern University</span>
                  </div>
                  <div>
                    <span className="special-msg">
                      <em>{this.props.globalState.specialMsg}</em>
                    </span>
                  </div>
                </td>
              </tr>
              <tr>
                <td
                  style={{
                    fontFamily: '"Arial"',
                    fontSize: "14px",
                    color: "#716C6B",
                    paddingTop: "10px",
                    paddingBottom: "30px",
                    lineHeight: "1.37",
                  }}
                >
                  {this.props.globalState.address != "None" && addressStr}
                  {this.props.globalState.phoneNumValidity.office && OfficeNum}
                  {this.props.globalState.phoneNumValidity.cell && CellNum}
                </td>
              </tr>
            </tbody>
          </table>
          <table
            cellPadding="0"
            cellSpacing="0"
            style={{
              fontFamily: '"Arial"',
              fontSize: "14px",
              color: "#716C6B",
            }}
          >
            <tbody>
              <tr>
                <td
                  style={{
                    fontFamily: '"Arial"',
                    fontSize: "14px",
                    color: "#4e2a84",
                    paddingTop: "10px",
                    lineHeight: "1.37",
                  }}
                >
                  <strong>
                    <a
                      style={{
                        color: "#4e2a84",
                      }}
                      href="https://wewill.northwestern.edu/s/1479/282-giving/index-campaign.aspx?gid=282&pgid=61&utm_medium=email&utm_source=ARD%20email&utm_campaign=ARD%20Email%20Signature"
                    >
                      We Will. The Campaign for Northwestern.
                    </a>{" "}
                    <br />
                    <a
                      style={{
                        color: "#4e2a84",
                      }}
                      href="https://www.alumni.northwestern.edu/s/1479/02-naa/16/home.aspx?sid=1479&gid=2&pgid=20761&utm_medium=email&utm_source=ARD%20email&utm_campaign=ARD%20Email%20Signature"
                    >
                      alumni.northwestern.edu
                    </a>
                  </strong>
                </td>
              </tr>
              <tr>
                <td
                  style={{
                    fontFamily: '"Arial"',
                    fontSize: "12px",
                    color: "#716C6B",
                    paddingTop: "10px",
                    lineHeight: "1.37",
                  }}
                >
                  <a
                    style={{ color: "#716C6B", textDecoration: "none" }}
                    href="https://www.facebook.com/northwesternalumni"
                  >
                    Facebook
                  </a>
                  {" | "}
                  <a
                    style={{ color: "#716C6B", textDecoration: "none" }}
                    href="https://twitter.com/NUAlumni"
                  >
                    Twitter
                  </a>
                  {" | "}
                  <a
                    style={{ color: "#716C6B", textDecoration: "none" }}
                    href="https://www.instagram.com/northwesternalumni/"
                  >
                    Instagram
                  </a>
                </td>
              </tr>
            </tbody>
          </table>
          {this.props.globalState.isAcknowledgement && landAcknowledgement}
        </div>
      </section>
    );
  }
}
